mapper.insert:
INSERT INTO TB_SEND2CAR ( SEND_TO_CAR_ID, MEMB_ID, MGRAPP_ID, TARGET_NM, TARGET_ADDRESS, TARGET_LONX, TARGET_LATY, SEND_STATUS, USE_YN,
ARRIV_HOPE_TIME,
EST_TIME, REG_ID,
UPD_ID, UPD_DT, SERVICE_TYPE, RSV_TYPE, SVR_ID, PROCESS_DT, TARGET_REAL_LONX, TARGET_REAL_LATY
,TARGET_POI_ID
,TARGET_ROAD_NAME
,TARGET_ROAD_JIBUN
,SEARCH_OPTION
) VALUES ( #{sendToCarId}, #{membId}, #{mgrappId}, #{targetNm}, #{targetAddress}, #{targetLonx}, #{targetLaty}, #{sendStatus}, 'Y',
TO_DATE(#{arrivHopeTime},'yyyy-MM-dd hh24:mi:ss'),
#{estTime,jdbcType=VARCHAR}, 'SYSTEM',
'SYSTEM', SYSDATE, #{serviceType}, #{rsvType}, #{svrId}, SYSDATE, #{targetRealLonx}, #{targetRealLaty}
,#{poiId}
,#{roadName}
,#{roadJibun}
,#{searchOption}
)

mapper.select:
SELECT
	COUNT(*)
FROM TB_SEND2CAR
WHERE 1=1

	AND MEMB_ID = #{membId}	


	AND MGRAPP_ID = #{mgrappId}	


	AND USE_YN = #{useYn}	

AND TARGET_LONX = #{targetLonx}
AND TARGET_LATY = #{targetLaty}
AND TARGET_NM = #{targetNm} 
AND PROCESS_DT >=  SYSDATE - 5/24/60

mapper.delete:
DELETE
	TB_SEND2CAR
WHERE MEMB_ID = #{membId} 
AND MGRAPP_ID = #{mgrappId} 
AND SCHEDULE_ID = #{scheduleId}
AND SEND_STATUS = #{sendStatus}

mapper.update:
UPDATE
	TB_SEND2CAR
SET 
USE_YN = #{useYn}
WHERE UPD_DT IN(SELECT UPD_DT
                  FROM TB_SEND2CAR
                 WHERE UPD_DT < (SYSDATE - 10)
                )  
AND USE_YN = 'Y'
AND MEMB_ID = #{membId} 
AND MGRAPP_ID = #{mgrappId}

UPDATE
	TB_SEND2CAR
SET 
USE_YN = 'N'
WHERE UPD_DT < (SELECT MIN(UPD_DT)
                  FROM (SELECT UPD_DT
                  		  FROM TB_SEND2CAR
                 		 WHERE MEMB_ID = #{membId}
                   		   AND MGRAPP_ID = #{mgrappId}
                   		   AND USE_YN = 'Y'
                 ORDER BY UPD_DT desc)
                 WHERE ROWNUM <= 99
                )  
AND USE_YN = 'Y'
AND MEMB_ID = #{membId} 
AND MGRAPP_ID = #{mgrappId}
